INSERT INTO `email_template_data` VALUES (1,NULL,NULL,'<p>Hi ${candidateName}, </p><p><br/></p><p><br/></p><p><br/></p><p>Thank you for applying with ${clientName}.  We are currently reviewing your experience and qualifications.  Should we determine that your profile meets our requirements, we will contact you with further inputs.</p><p><br/></p><p><br/></p><p><br/></p><p>We invite you to visit our website to learn more about our selection process, our people, our culture and how to connect with our social networks and talent communities.</p><p><br/></p> <p><br/></p><p>Thank you for your interest in career opportunities with ${clientName}.</p><p><br/></p><p><br/></p><p><br/></p><p>Regards, </p><p><br/></p><p>${hrName}</p><p><br/></p><p>${hrMobile}</p><p><br/></p>\n\n','Application Responses','Auto response','apply');
INSERT INTO `email_template_data` VALUES (2,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/>Hope you\'re doing good ! <br/>I came across your Profile and found it to be quite interesting. We understand that you are looking for new opportunities and would like to connect with you to discuss an opportunity into ${clientName} as ${positionName}. <br/><br/>Please do let me know a suitable time along with your contact details so as to discuss and network further. Feel free to reach out to me for any concerns. <br/><br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Candidate Reach-out','Profile Shortlisted for ${positionName} at ${clientName}','interview');
INSERT INTO `email_template_data` VALUES (3,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/><br/>It was great talking to you about current opportunities with ${clientName}. <br/>As part of the Interview cycle weâ€™d like to inform you, that your Profile has been shortlisted for further evaluation. We are scheduling  your Telephonic Interview schedule details along with this. <br/>Kindly confirm your availability for the same. <br/><br/>    In case of any queries, feel free to reach out to us.<br/>POC Name  : <br/>Contact#     : <br/>E-mail             :<br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Telephonic Screening','Telephonic Screening for ${positionName} at ${clientName}','interview');
INSERT INTO `email_template_data` VALUES (4,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/><br/>As part of the Interview cycle, your Profile is being assessed and moved to the next (Technical) Interview round - Online Coding / Video Call / Online Assignment Test session and is scheduled as mentioned below.  <br/><br/><br/>To Connect to Online Meeting : <br/>From your Computer, please join online meeting using ____Browser. <br/>Go to :    [ additional info ] <br/>----------OR--------<br/>From your Computer, please join online meeting using Skype / Google Hangout / others. <br/>Add User :  <br/>Use your microphone and speakers (VoIP) for audio. You\'ll sound best with a headset. <br/><br/><br/>Please ensure you are having uninterrupted Internet connectivity throughout the session and we request you to be available about 5 minutes before the call. <br/>In case of any concern, feel free to reach out to us. <br/>POC Name  : <br/>Contact#     : <br/>E-mail             : <br/><br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Online: Video Call / Online Assignment Test','Online: Video Call / Online Assignment Test for ${positionName} at ${clientName}','interview');
INSERT INTO `email_template_data` VALUES (5,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/><br/>Your Face-to-Face meeting has been scheduled / rescheduled. Please find the details mentioned here. <br/><br/><br/>Office location: <br/>Organization Name:  <br/>Complete address:<br/>City:<br/>(Landmark: ) <br/>Google Map Location: <br/>Kindly try and reach about 5-10 minutes before scheduled time. <br/>Feel free to connect with us if you\'re unable to locate the Office. <br/>POC Name  : <br/>Contact#     : <br/>E-mail             : <br/><br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Face-to-Face Meetings','Face-to-Face Meetings for ${positionName} at ${clientName}','interview');
INSERT INTO `email_template_data` VALUES (6,NULL,NULL,'<p>Hi ${candidateName}, <br/>Congratulations on your Offer !<br/>Further to your discussions / meetings with us, we are pleased to Offer you the position of ${positionName} at ${clientName}.<br/><br/><br/>Your Offer Letter is attached with this e-mail and your tentative Date of Joining is being considered as DD-MMM-YYYY. <br/><br/><br/>Please revert to this mail by tomorrow with your confirmation regarding the acceptance of Offer. <br/>Feel free to get in touch with us for any further clarifications. <br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Offer Letter','Offer Letter for ${positionName} at ${clientName}','email');
INSERT INTO `email_template_data` VALUES (7,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/><br/>This is with regards to your Interview rounds with ${clientName} and we appreciate you taking the time to Interview with us.  After careful consideration, we regret to inform you that your Profile has not been selected for this position.<br/> <br/>However your information will remain in our system for any other job opportunities in future and we hope our paths cross again. <br/><br/><br/>Thank you for your interest in career opportunities with ${clientName}. <br/><br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}</p>','Reject Mailer','Application rejected for ${positionName} at ${clientName}','email');
INSERT INTO `email_template_data` VALUES (8,NULL,NULL,'<p>Hi ${candidateName}, <br/><br/><br/>As part of Joining formalities, you are requested you to submit a Scan Copy of the following documents.<br/><br/><br/>1. Relieving Letter / Resignation acceptance letter from your current Employer<br/>2. Previous Employment letters<br/>3. Education Certificates<br/>4. PAN Card<br/>5. Aadhaar Card / Govt. issued Photo ID Card  <br/>6. Date of Birth details <br/><br/><br/><br/><br/>Regards, <br/>${hrName}<br/>${hrMobile}<br/><br/></p>','Docs Submission','Docs Submission Required for ${positionName} at ${clientName}','email');
