UPDATE `email_template_data` SET `body`='<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n	<meta http-equiv=\"content-type\" content=\"text/html; charset=utf-8\">\n  	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0;\">\n 	<meta name=\"format-detection\" content=\"telephone=no\"/>\n\n	<!-- Responsive Mobile-First Email Template by Konstantin Savchenko, 2015.\n	https://github.com/konsav/email-templates/  -->\n\n	<style>\n/* Reset styles */ \nbody { margin: 0; padding: 0; min-width: 100%; width: 100% !important; height: 100% !important;}\nbody, table, td, div, p, a { -webkit-font-smoothing: antialiased; text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-text-size-adjust: 100%; line-height: 100%; }\ntable, td { mso-table-lspace: 0pt; mso-table-rspace: 0pt; border-collapse: collapse !important; border-spacing: 0; }\nimg { border: 0; line-height: 100%; outline: none; text-decoration: none; -ms-interpolation-mode: bicubic; }\n#outlook a { padding: 0; }\n.ReadMsgBody { width: 100%; } .ExternalClass { width: 100%; }\n.ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div { line-height: 100%; }\n\n/* Rounded corners for advanced mail clients only */ \n@media all and (min-width: 560px) {\n	.container { border-radius: 8px; -webkit-border-radius: 8px; -moz-border-radius: 8px; -khtml-border-radius: 8px;}\n}\n\n/* Set color for auto links (addresses, dates, etc.) */ \na, a:hover {\n	color: #127DB3;\n}\n.footer a, .footer a:hover {\n	color: #999999;\n}\n\n 	</style>\n\n	<!-- MESSAGE SUBJECT -->\n	<title>Recruiz</title>\n\n</head>\n\n<!-- BODY -->\n<!-- Set message background color (twice) and text color (twice) -->\n<body topmargin=\"0\" rightmargin=\"0\" bottommargin=\"0\" leftmargin=\"0\" marginwidth=\"0\" marginheight=\"0\" width=\"100%\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0; width: 100%; height: 100%; -webkit-font-smoothing: antialiased; text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-text-size-adjust: 100%; line-height: 100%;\n	background-color: #F0F0F0;\n	color: #000000;\"\n	bgcolor=\"#F0F0F0\"\n	text=\"#000000\">\n\n<!-- SECTION / BACKGROUND -->\n<!-- Set message background color one again -->\n<table width=\"100%\" align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0; width: 100%;\" class=\"background\"><tr><td align=\"center\" valign=\"top\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0;\"\n	bgcolor=\"#F0F0F0\">\n\n<!-- WRAPPER -->\n<!-- Set wrapper width (twice) header -->\n${header}\n\n<!-- WRAPPER / CONTEINER -->\n<!-- Set conteiner background color -->\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" align=\"center\"\n	bgcolor=\"#FFFFFF\"\n	width=\"560\" style=\"border-collapse: collapse; border-spacing: 0; padding: 0; width: inherit;\n	max-width: 560px;\" class=\"container\">\n\n	<!-- HERO IMAGE -->\n	<!-- Image text color should be opposite to background color. Set your url, image src, alt and title. Alt text should fit the image size. Real image size should be x2 (wrapper x2). Do not set height for flexible images (including \"auto\"). URL format: http://domain.com/?utm_source={{Campaign-Source}}&utm_medium=email&utm_content={{ÃŒmage-Name}}&utm_campaign={{Campaign-Name}} -->\n	\n	<!-- PARAGRAPH -->\n	<!-- Set text color and font family (\"sans-serif\" or \"Georgia, serif\"). Duplicate all text styles in links, including line-height -->\n	<tr>\n		<td align=\"left\" valign=\"top\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0; padding-left: 6.25%; padding-right: 6.25%; width: 87.5%; font-size: 17px; font-weight: 400; line-height: 160%;\n			padding-top: 25px; \n			color: #000000;\n			font-family: sans-serif;\" class=\"paragraph\">\n				${emailBody}\n		</td>\n	</tr>\n\n	<!-- BUTTON -->\n	<!-- Set button background color at TD, link/text color at A and TD, font family (\"sans-serif\" or \"Georgia, serif\") at TD. For verification codes add \"letter-spacing: 5px;\". Link format: http://domain.com/?utm_source={{Campaign-Source}}&utm_medium=email&utm_content={{Button-Name}}&utm_campaign={{Campaign-Name}} -->\n	<tr>\n		<td align=\"center\" valign=\"top\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0; padding-left: 6.25%; padding-right: 6.25%; width: 87.5%;\n			padding-top: 25px;\n			padding-bottom: 5px;\" class=\"button\"><a\n			href=\"${noteLink}\" target=\"_blank\" style=\"text-decoration: none;\">\n				<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" align=\"center\" style=\"max-width: 240px; min-width: 120px; border-collapse: collapse; border-spacing: 0; padding: 0;\"><tr><td align=\"center\" valign=\"middle\" style=\"padding: 12px 24px; margin: 0; text-decoration:  none; border-collapse: collapse; border-spacing: 0; border-radius: 4px; -webkit-border-radius: 4px; -moz-border-radius: 4px; -khtml-border-radius: 4px;\"\n					bgcolor=\"#29bbb0\"><a target=\"_blank\" style=\"text-decoration: none;\n					color: #FFFFFF; font-family: sans-serif; font-size: 17px; font-weight: 400; line-height: 120%;\"\n					href=\"${noteLink}\">\n						${buttonText}\n					</a>\n			</td></tr></table></a>\n		</td>\n	</tr>\n\n	<!-- LINE -->\n	<!-- Set line color -->\n	\n\n	<!-- LINE -->\n	<!-- Set line color -->\n	<tr>\n		<td align=\"center\" valign=\"top\" style=\"border-collapse: collapse; border-spacing: 0; margin: 0; padding: 0; padding-left: 6.25%; padding-right: 6.25%; width: 87.5%;\n			padding-top: 25px;\" class=\"line\">\n		</td>\n	</tr>\n\n	<!-- PARAGRAPH -->\n	\n\n<!-- End of WRAPPER -->\n</table>\n\n<!-- WRAPPER -->\n<!-- Set wrapper width (twice) footer -->\n${footer}\n\n<!-- End of SECTION / BACKGROUND -->\n</td></tr></table>\n\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" align=\"center\" width=\"100%\" style=\"border-collapse: collapse; border-spacing: 0; padding: 0; width: inherit; max-width:100%;\" class=\"wrapper\">\n<tr>\n  <td><h2 class=\"stripe-1\" style=\"height: 5px; margin-bottom: 0px; background: repeating-linear-gradient( 145deg, #29bbb0, #29bbb0 20px, #d9dc2e  20px, #d9dc2e  40px);\"></h2></td>\n</tr>\n\n</table>\n\n</body>\n</html>' WHERE `id`='21';
